    DECLARE imageIdRepository INT;
    DECLARE imageIdEnterprise INT;    
    DECLARE imageIdMaster INT;
    DECLARE imageName VARCHAR(255);
    DECLARE isStateful TINYINT(1);
    DECLARE totalDisksSizeBytes BIGINT DEFAULT 0;
    
    SELECT CASE WHEN EXISTS (select d.disk_id from disk d where d.template_id = OLD.template_id and d.stateful = 1) then 1 else 0 end  INTO isStateful;    
    
    IF (isStateful=0) THEN
        SELECT vi.idRepository, vi.name, vi.idMaster, vi.idEnterprise, IFNULL(SUM(d.diskFileSize),0) INTO imageIdRepository, imageName, imageIdMaster, imageIdEnterprise, totalDisksSizeBytes
        FROM virtualimage vi
            JOIN repository r on (vi.idRepository = r.idRepository)
            JOIN disk d on (vi.idImage = d.template_id)
        WHERE d.state = 'DONE' AND d.stateful = false AND vi.idImage=OLD.template_id;
    END IF;
    
    IF (imageIdEnterprise > 0) AND (totalDisksSizeBytes > 0) AND
            EXISTS( SELECT * FROM INFORMATION_SCHEMA.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingRepositoryRegisterEvents' ) THEN
        
        CALL kinton_accounting.AccountingRepositoryRegisterEvents('UPDATE_IMAGE',
                                                        OLD.template_id, imageIdRepository, imageName, "Unknown",
                                                        IF(imageIdMaster IS NULL,'TEMPLATE','INSTANCE'), totalDisksSizeBytes, imageIdEnterprise);
