DROP TRIGGER IF EXISTS `update_nat_ip_update_stats`;
DELIMITER ;;
CREATE TRIGGER `update_nat_ip_update_stats` AFTER UPDATE ON `nat_ip` FOR EACH ROW BEGIN
        DECLARE idVirtualDataCenterObj INTEGER;
        DECLARE idEnterpriseObj INTEGER;
        CALL DebugLog(CONCAT('Entering update_nat_ip_update_stats oldvdc ',IFNULL(OLD.id_virtualdatacenter,'null'), ' newvdc ', IFNULL(NEW.id_virtualdatacenter,'null'), ' for ip ', IFNULL(NEW.ip,'null')));
        
        IF OLD.id_virtualdatacenter IS NULL AND NEW.id_virtualdatacenter IS NOT NULL THEN
            
            SELECT vdc.idVirtualDataCenter, vdc.idEnterprise  INTO idVirtualDataCenterObj, idEnterpriseObj
            FROM nat_ip nat, virtualdatacenter vdc
            WHERE vdc.idVirtualDataCenter = nat.id_virtualdatacenter
                AND NEW.id_nat_ip = nat.id_nat_ip;
            
            IF (idVirtualDataCenterObj IS NOT NULL AND idEnterpriseObj IS NOT NULL) THEN
                CALL DebugLog(CONCAT('Reserved NAT IP: enterprise ',IFNULL(idEnterpriseObj,'entnull'), ' vdc ', IFNULL(idVirtualDataCenterObj,'vdcnull')));
                IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingIPsRegisterEvents' ) THEN                        
                  CALL kinton_accounting.AccountingIPsRegisterEvents('IP_RESERVED',NEW.id_nat_ip, NEW.ip,idVirtualDataCenterObj, idEnterpriseObj,'NAT_IP');
                END IF;
            END IF;
        END IF;
        
        IF OLD.id_virtualdatacenter IS NOT NULL AND NEW.id_virtualdatacenter IS NULL THEN
            SELECT vdc.idEnterprise  INTO idEnterpriseObj
            FROM virtualdatacenter vdc
            WHERE vdc.idVirtualDataCenter = OLD.id_virtualdatacenter;
            
            CALL DebugLog(CONCAT('FREED NAT IP: enterprise ',IFNULL(idEnterpriseObj,'entnull'), ' vdc ', IFNULL(idVirtualDataCenterObj,'vdcnull')));
            IF EXISTS( SELECT * FROM information_schema.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingIPsRegisterEvents' ) THEN
                CALL kinton_accounting.AccountingIPsRegisterEvents('IP_FREED',OLD.id_nat_ip,OLD.ip,OLD.id_virtualdatacenter, idEnterpriseObj,'NAT_IP');
            END IF;
        END IF;
    END
;;
