DELIMITER ;
DROP TRIGGER IF EXISTS `update_rasd_management_update_stats`;
DELIMITER ;;
CREATE TRIGGER `update_rasd_management_update_stats` AFTER UPDATE ON `rasd_management` FOR EACH ROW BEGIN
        DECLARE idDataCenterObj INTEGER;
        DECLARE idEnterpriseObj INTEGER;
        DECLARE ipAddress VARCHAR(20) CHARACTER SET utf8;
        DECLARE v_idTier INTEGER;
        DECLARE v_elementName VARCHAR(256) CHARACTER SET utf8;
        DECLARE v_limitResource BIGINT(20);

        IF (@DISABLE_STATS_TRIGGERS IS NULL)
        THEN
            
            IF OLD.idResourceType = 10 AND OLD.idVirtualDataCenter IS NOT NULL AND NEW.idVirtualDataCenter IS NULL
            THEN
                
                SELECT dc.abstract_datacenter_id, ipm.ip INTO idDataCenterObj, ipAddress
                FROM ip_pool_management ipm,
                     network_configuration nc,
                     vlan_network vn,
                     abstract_datacenter dc
                WHERE ipm.vlan_network_id = vn.vlan_network_id
                  AND vn.network_configuration_id = nc.network_configuration_id
                  AND vn.network_id = dc.network_id
                  AND vn.networktype = 'PUBLIC'
                  AND OLD.idManagement = ipm.idManagement;

                IF idDataCenterObj IS NULL
                THEN
                    
                    SELECT dc.abstract_datacenter_id, ipm.ip INTO idDataCenterObj, ipAddress
                    FROM ip_pool_management ipm,
                         abstract_datacenter dc,
                         virtualdatacenter vdc
                    WHERE OLD.idVirtualDataCenter = vdc.idVirtualDataCenter
                      AND dc.abstract_datacenter_id = vdc.idDataCenter
                      AND ipm.vlan_network_id IS NULL
                      AND OLD.idManagement = ipm.idManagement;
                END IF;

                
                IF idDataCenterObj IS NOT NULL
                THEN
                    
                    SELECT vdc.idEnterprise INTO idEnterpriseObj
                    FROM virtualdatacenter vdc
                    WHERE vdc.idVirtualDataCenter = OLD.idVirtualDataCenter;
                    IF EXISTS(SELECT *
                              FROM information_schema.ROUTINES
                              WHERE ROUTINE_SCHEMA = 'kinton_accounting'
                                AND ROUTINE_TYPE = 'PROCEDURE'
                                AND ROUTINE_NAME = 'AccountingIPsRegisterEvents')
                    THEN
                        CALL kinton_accounting.AccountingIPsRegisterEvents('IP_FREED', OLD.idManagement, ipAddress,
                                                                           OLD.idVirtualDataCenter, idEnterpriseObj,
                                                                           'PUBLIC_IP');
                    END IF;
                END IF;
            END IF;

            
            IF (OLD.idResourceType = 8) AND
               (OLD.idVirtualDataCenter IS NOT NULL) AND (NEW.idVirtualDataCenter != OLD.idVirtualDataCenter)
            THEN

                SELECT vdc.idEnterprise INTO idEnterpriseObj
                FROM virtualdatacenter vdc
                WHERE vdc.idVirtualDataCenter = NEW.idVirtualDataCenter;

                SELECT r.elementName, r.limitResource INTO v_elementName, v_limitResource
                FROM rasd r
                WHERE r.instanceID = OLD.idResource;

                SELECT sp.idTier INTO v_idTier
                FROM kinton.rasd r,
                     kinton.rasd_management rm,
                     kinton.volume_management volMgmt,
                     kinton.storage_pool sp
                WHERE rm.idResource = OLD.idResource
                  AND r.instanceID = rm.idResource
                  AND r.resourcetype = 8
                  AND volMgmt.idManagement = rm.idManagement
                  AND volMgmt.idStorage = sp.idStorage;
                CALL kinton_accounting.AccountingStorageRegisterEvents('UPDATE_STORAGE', OLD.idResource, v_elementName,
                                                                       v_idTier, NEW.idVirtualDataCenter,
                                                                       idEnterpriseObj, v_limitResource);
            END IF;
            
            IF (NEW.idVM IS NOT NULL AND OLD.idVM IS NULL)
             THEN
                 IF NEW.idResourceType = 17
                 THEN
                     CALL DebugLog(CONCAT('update_rasd_management_update_stats: disk added to vm ', NEW.idVM));
                     CALL kinton_accounting.ForceAccountingVMUpdateValues(NEW.idVM);
                 END IF;
             END IF;            
        END IF;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `delete_rasd_management_update_stats`;
DELIMITER ;;
CREATE TRIGGER `delete_rasd_management_update_stats` AFTER DELETE ON `rasd_management` FOR EACH ROW BEGIN
        DECLARE idThisEnterprise INTEGER;
        DECLARE limitResourceObj BIGINT;
        DECLARE resourceName VARCHAR(255);
        
        SELECT vdc.idEnterprise INTO idThisEnterprise
        FROM virtualdatacenter vdc
        WHERE vdc.idVirtualDataCenter = OLD.idVirtualDataCenter;
        
        SELECT elementName, limitResource INTO resourceName, limitResourceObj
        FROM rasd r
        WHERE r.instanceID = OLD.idResource;
        
        IF (@DISABLE_STATS_TRIGGERS IS NULL)
        THEN
            IF (OLD.idResourceType = '8') AND (OLD.temporal IS NULL)
            THEN
                IF EXISTS(SELECT *
                          FROM `information_schema`.ROUTINES
                          WHERE ROUTINE_SCHEMA = 'kinton_accounting'
                            AND ROUTINE_TYPE = 'PROCEDURE'
                            AND ROUTINE_NAME = 'AccountingStorageRegisterEvents')
                THEN
                    CALL kinton_accounting.AccountingStorageRegisterEvents('DELETE_STORAGE', OLD.idResource,
                                                                           resourceName, 0, OLD.idVirtualDataCenter,
                                                                           idThisEnterprise, limitResourceObj);
                END IF;
            END IF;
            IF (OLD.idResourceType = 17)
                THEN
                    CALL DebugLog(CONCAT('delete_rasd_management_update_stats: disk was removed from vm ', OLD.idVM));                
                    CALL kinton_accounting.ForceAccountingVMUpdateValues(OLD.idVM);
            END IF;
        END IF;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `virtualdatacenter_updated`;
DELIMITER ;;
CREATE TRIGGER `virtualdatacenter_updated` AFTER UPDATE ON `virtualdatacenter` FOR EACH ROW BEGIN
		DECLARE vlanNetworkIdObj INTEGER;    
		DECLARE networkNameObj VARCHAR(255);
        IF (@DISABLE_STATS_TRIGGERS IS NULL) THEN           
		    IF OLD.networktypeID IS NOT NULL AND NEW.networktypeID IS NULL THEN
		    
			BEGIN
				DECLARE done INTEGER DEFAULT 0;
				DECLARE cursorVlan CURSOR FOR SELECT DISTINCT vn.vlan_network_id, vn.network_name FROM vlan_network vn WHERE vn.network_id = OLD.networktypeID;
				DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;				
				OPEN cursorVlan;				
				REPEAT
					FETCH cursorVlan into vlanNetworkIdObj, networkNameObj;
				   	IF NOT done THEN
						IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingVLANRegisterEvents' ) THEN							
							CALL kinton_accounting.AccountingVLANRegisterEvents('DELETE_VLAN',vlanNetworkIdObj, networkNameObj, OLD.idVirtualDataCenter,OLD.idEnterprise);
					   	END IF;    
					END IF;
					UNTIL done END REPEAT;
					CLOSE cursorVlan;				 	
			END ;
		    END IF;
        END IF;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `virtualdatacenter_deleted`;
DELIMITER ;;
CREATE TRIGGER `virtualdatacenter_deleted` BEFORE DELETE ON `virtualdatacenter` FOR EACH ROW BEGIN
    DECLARE currentIdManagement INTEGER DEFAULT -1;
    DECLARE currentIpAddress VARCHAR(20) DEFAULT '';
    DECLARE no_more_ipsfreed INT;
    DECLARE curIpFreed CURSOR FOR SELECT ipm.ip, ra.idManagement   
       FROM ip_pool_management ipm, network_configuration nc, vlan_network vn, rasd_management ra
       WHERE ipm.vlan_network_id = vn.vlan_network_id
       AND vn.network_configuration_id = nc.network_configuration_id
       AND vn.networktype = 'PUBLIC'
       AND ra.idManagement = ipm.idManagement
       AND ra.idVirtualDataCenter = OLD.idVirtualDataCenter;
       
       DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more_ipsfreed = 1;   
       IF (@DISABLE_STATS_TRIGGERS IS NULL) THEN
			SET no_more_ipsfreed = 0;       
		    OPEN curIpFreed;            
		    my_loop:WHILE(no_more_ipsfreed=0) DO 
				FETCH curIpFreed INTO currentIpAddress, currentIdManagement;
				IF no_more_ipsfreed=1 THEN
				            LEAVE my_loop;
				     END IF;
				
				UPDATE ip_pool_management set mac=NULL, name=NULL WHERE idManagement = currentIdManagement;
				IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingIPsRegisterEvents' ) THEN
					CALL kinton_accounting.AccountingIPsRegisterEvents('IP_FREED',currentIdManagement,currentIpAddress,OLD.idVirtualDataCenter, OLD.idEnterprise);
				END IF;                    
        	END WHILE my_loop;         
        CLOSE curIpFreed;
        END IF;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `virtualimage_insert`;
DELIMITER ;;
CREATE TRIGGER `virtualimage_insert` AFTER INSERT ON `virtualimage` FOR EACH ROW BEGIN
        DECLARE isStateful TINYINT(1);
        DECLARE totalDisksSizeBytes BIGINT DEFAULT 0;
        SELECT CASE WHEN EXISTS (select d.disk_id from disk d where d.template_id = NEW.idImage and d.stateful = 1) then 1 else 0 end  INTO isStateful;
        
        
        
        
            IF ((NEW.idEnterprise > 0) AND (isStateful=0)) AND
                EXISTS( SELECT * FROM INFORMATION_SCHEMA.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingRepositoryRegisterEvents' ) THEN
                SELECT IFNULL(SUM(d.diskFileSize),0) INTO totalDisksSizeBytes
                FROM virtualimage vi
                    JOIN repository r on (vi.idRepository = r.idRepository)
                    JOIN disk d on (vi.idImage = d.template_id)
                WHERE d.state = 'DONE' AND d.stateful = false AND vi.idImage=NEW.idImage;
                
                
                CALL kinton_accounting.AccountingRepositoryRegisterEvents('ADD_IMAGE',
                                                        NEW.idImage, NEW.idRepository, NEW.name, "Unknown",
                                                        IF(NEW.idMaster IS NULL,'TEMPLATE','INSTANCE'), totalDisksSizeBytes, NEW.idEnterprise);
            END IF;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `virtualimage_update`;
DELIMITER ;;
CREATE TRIGGER `virtualimage_update` AFTER UPDATE ON `virtualimage` FOR EACH ROW BEGIN
    DECLARE idDummy BIGINT; 
    DECLARE isStateful TINYINT(1);
    DECLARE totalDisksSizeBytes BIGINT DEFAULT 0;
    
    
    SELECT CASE WHEN EXISTS (select d.disk_id from disk d where d.template_id = NEW.idImage and d.stateful = 1) then 1 else 0 end  INTO isStateful;
    
    
    
    IF ((NEW.idEnterprise > 0) AND (isStateful=0)) AND
        EXISTS( SELECT * FROM INFORMATION_SCHEMA.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingRepositoryRegisterEvents' ) THEN
        
        SELECT IFNULL(SUM(d.diskFileSize),0) INTO totalDisksSizeBytes
        FROM virtualimage vi
            JOIN repository r on (vi.idRepository = r.idRepository)
            JOIN disk d on (vi.idImage = d.template_id)
        WHERE d.state = 'DONE' AND d.stateful = false AND vi.idImage=NEW.idImage;
        
        
        IF (totalDisksSizeBytes > 0) THEN
            
            CALL kinton_accounting.AccountingRepositoryRegisterEvents('UPDATE_IMAGE',
                                                    NEW.idImage, NEW.idRepository, NEW.name, "Unknown",
                                                    IF(NEW.idMaster IS NULL,'TEMPLATE','INSTANCE'), totalDisksSizeBytes, NEW.idEnterprise);
        END IF;
        
        IF (NEW.cost_code != OLD.cost_code) OR (OLD.cost_code IS NULL AND NEW.cost_code IS NOT NULL) OR (OLD.cost_code IS NOT NULL AND NEW.cost_code IS NULL) THEN
            
            
        BEGIN
            DECLARE v_idVM INTEGER UNSIGNED;
            DECLARE v_idVapp INTEGER UNSIGNED;
            DECLARE v_cpu INTEGER UNSIGNED;
            DECLARE v_ram INTEGER UNSIGNED;
            DECLARE v_hd BIGINT UNSIGNED;
            DECLARE v_hp INTEGER UNSIGNED;
            DECLARE v_hp_costcode INTEGER UNSIGNED;
            DECLARE done INT DEFAULT FALSE;
            DECLARE cur1 CURSOR FOR SELECT vma.idVM, vm.virtual_appliance, vma.cpu, vma.ram, vma.hd, vma.idHardwareProfile
                                    FROM kinton_accounting.accounting_event_vm vma, virtualmachine vm
                                    WHERE   vm.idImage=NEW.idImage AND
                                            vma.idVM=vm.idVM AND vma.stopTime IS NULL;
            DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
            SET done = false;
            OPEN cur1;
            read_loop: LOOP
                SET done = false;
                FETCH cur1 INTO v_idVM, v_idVapp, v_cpu, v_ram, v_hd, v_hp;
                IF done THEN
                  LEAVE read_loop;
                END IF;

                SELECT IFNULL(hpad.cost_code_id,'') INTO v_hp_costcode 
                FROM hardware_profile hp, hardware_profile_abs_datacenter hpad 
                WHERE hp.hardware_profile_abs_datacenter_id = hpad.id
                    AND hp.id = v_hp;

                 IF (v_hp_costcode IS NULL OR v_hp_costcode = '0') THEN
                    CALL kinton_accounting.AccountingVMRegisterEvents(v_idVM, v_idVapp, 1, "N/A", "COST_CODE_UPDATE", "N/A", v_ram, v_cpu, v_hd, NEW.cost_code, v_hp);
                    CALL kinton_accounting.AccountingCostCodeUpdate(v_idVM, NEW.cost_code);
                    CALL kinton_accounting.AccountingVMBackupRegisterEvents("UPDATE_BACKUP", v_idVM, NULL);
                END IF;
                IF (v_hp_costcode IS NOT NULL AND v_hp_costcode != '0') THEN
                    CALL kinton_accounting.AccountingVMRegisterEvents(v_idVM, v_idVapp, 1, "N/A", "COST_CODE_UPDATE", "N/A", v_ram, v_cpu, v_hd, v_hp_costcode, v_hp);
                    CALL kinton_accounting.AccountingCostCodeUpdate(v_idVM, v_hp_costcode);
                    CALL kinton_accounting.AccountingVMBackupRegisterEvents("UPDATE_BACKUP", v_idVM, NULL);
                END IF;
            END LOOP;
            CLOSE cur1;
        END ;
        END IF;
    END IF;
END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `virtualimage_delete`;
DELIMITER ;;
CREATE TRIGGER `virtualimage_delete` AFTER DELETE ON `virtualimage` FOR EACH ROW BEGIN
    DECLARE isStateful TINYINT(1);
    DECLARE totalDisksSizeBytes BIGINT DEFAULT 0;
    
    SELECT CASE WHEN EXISTS (select d.disk_id from disk d where d.template_id = OLD.idImage and d.stateful = 1) then 1 else 0 end  INTO isStateful;
    
    SELECT IFNULL(SUM(d.diskFileSize),0) INTO totalDisksSizeBytes
        FROM virtualimage vi
            JOIN repository r on (vi.idRepository = r.idRepository)
            JOIN disk d on (vi.idImage = d.template_id)
        WHERE d.state = 'DONE' AND d.stateful = false AND vi.idImage=OLD.idImage;
    
    IF ((OLD.idEnterprise > 0) AND (isStateful=0)) AND
        EXISTS( SELECT * FROM INFORMATION_SCHEMA.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingRepositoryRegisterEvents' ) THEN
        
        CALL kinton_accounting.AccountingRepositoryRegisterEvents('DELETE_IMAGE',
            OLD.idImage, OLD.idRepository, OLD.name, "UNKNOWN",
            IF(OLD.idMaster IS NULL,'TEMPLATE','INSTANCE'), totalDisksSizeBytes, OLD.idEnterprise);
    END IF;
END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `create_vlan_network_update_stats`;
DELIMITER ;;
CREATE TRIGGER `create_vlan_network_update_stats` AFTER INSERT ON `vlan_network` FOR EACH ROW BEGIN
    DECLARE idVirtualDataCenterObj INTEGER;
    DECLARE idEnterpriseObj INTEGER;
    SELECT vdc.idVirtualDataCenter, e.idEnterprise INTO idVirtualDataCenterObj, idEnterpriseObj
    FROM virtualdatacenter vdc, enterprise e
    WHERE vdc.networktypeID=NEW.network_id
    AND vdc.idEnterprise=e.idEnterprise;
    IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingVLANRegisterEvents' ) THEN
    	IF  NEW.networktype IN ('INTERNAL') THEN
			CALL kinton_accounting.AccountingVLANRegisterEvents('CREATE_VLAN',NEW.vlan_network_id, NEW.network_name, idVirtualDataCenterObj, idEnterpriseObj);
		END IF;
    END IF;    
END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `delete_vlan_network_update_stats`;
DELIMITER ;;
CREATE TRIGGER `delete_vlan_network_update_stats` AFTER DELETE ON `vlan_network` FOR EACH ROW BEGIN
		DECLARE idVirtualDataCenterObj INTEGER;
		DECLARE idEnterpriseObj INTEGER;
		IF (@DISABLE_STATS_TRIGGERS IS NULL) THEN   
		        
		        SELECT vdc.idVirtualDataCenter, e.idEnterprise INTO idVirtualDataCenterObj, idEnterpriseObj
		        FROM virtualdatacenter vdc, enterprise e
		        WHERE vdc.networktypeID=OLD.network_id
		        AND vdc.idEnterprise=e.idEnterprise;
		        IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingVLANRegisterEvents' ) THEN		        	
		            CALL kinton_accounting.AccountingVLANRegisterEvents('DELETE_VLAN',OLD.vlan_network_id, OLD.network_name, idVirtualDataCenterObj, idEnterpriseObj);
		        END IF;
		    END IF;
	END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `create_volume_management_update_stats`;
DELIMITER ;;
CREATE TRIGGER `create_volume_management_update_stats` AFTER INSERT ON `volume_management` FOR EACH ROW BEGIN
        DECLARE idVirtualDataCenterObj INTEGER;
        DECLARE idThisEnterprise INTEGER;
        DECLARE limitResourceObj BIGINT;
        DECLARE idResourceObj VARCHAR(50);
        DECLARE idResourceTypeObj VARCHAR(5);
        DECLARE idStorageTier INTEGER;
        DECLARE resourceName VARCHAR(255);
        DECLARE temporal INTEGER;

        SELECT vdc.idEnterprise, vdc.idVirtualDataCenter, rm.temporal INTO idThisEnterprise, idVirtualDataCenterObj, temporal
        FROM virtualdatacenter vdc, rasd_management rm
        WHERE vdc.idVirtualDataCenter = rm.idVirtualDataCenter
        AND NEW.idManagement = rm.idManagement;
        
        IF temporal IS NULL THEN 
            SELECT r.elementName, r.limitResource, rm.idResource, rm.idResourceType INTO resourceName, limitResourceObj, idResourceObj, idResourceTypeObj
            FROM rasd r, rasd_management rm
            WHERE r.instanceID = rm.idResource
            AND NEW.idManagement = rm.idManagement;
            
            SELECT sp.idTier INTO idStorageTier
            FROM storage_pool sp
            WHERE sp.idStorage = NEW.idStorage;
            IF (@DISABLE_STATS_TRIGGERS IS NULL) THEN           
                IF idResourceTypeObj='8' THEN 
                    IF EXISTS( SELECT * FROM `information_schema`.ROUTINES WHERE ROUTINE_SCHEMA='kinton_accounting' AND ROUTINE_TYPE='PROCEDURE' AND ROUTINE_NAME='AccountingStorageRegisterEvents' ) THEN
                        CALL kinton_accounting.AccountingStorageRegisterEvents('CREATE_STORAGE', idResourceObj, resourceName, idStorageTier, idVirtualDataCenterObj, idThisEnterprise, limitResourceObj);
                    END IF;               
                END IF;
            END IF;
        END IF;
    END
;;
DELIMITER ;
